                        var scene = new ScrollMagic.Scene({
                            offset:50 ,
                            duration: 520,
                            })
                                .setTween(tween)
                                .addTo(controller);



                                var scroll = new SmoothScroll();
                                var anchor = document.querySelector('.image');
                                scroll.animateScroll(anchor);

    };



